from sha3 import keccak_256
from random import SystemRandom

cryptogen = SystemRandom()
salt_limit = 2 ** 256

deployer_address = bytes.fromhex('1f2afbd9c55ab4eb5bbc1c8e8239e7839223ce74')
init_code_hash = keccak_256(
  bytes.fromhex('')
).digest()

while True:
  salt = cryptogen.randrange(salt_limit).to_bytes(32, byteorder='big')
  contract_address = keccak_256(bytes.fromhex('FF') + deployer_address + salt + init_code_hash).digest()[12:].hex()

  if contract_address.find('badc0de') >= 0:
    print('salt: ', '0x' + salt.hex())
    print('eth_address: ', '0x' + contract_address)
    break;
